// Normalize all feed items into a unified shape
const items = $input.all();
const today = new Date();
const cutoff = new Date(today.getTime() - 24 * 60 * 60 * 1000); // last 24h

const keywords = [
  'AI', 'automation', 'LLM', 'agent', 'GPT', 'Claude', 'Gemini',
  'workflow', 'n8n', 'Zapier', 'Make', 'OpenAI', 'Anthropic',
  'business', 'enterprise', 'productivity', 'tool', 'SaaS',
  'chatbot', 'copilot', 'model', 'launch', 'release'
];

const seen = new Set();
const results = [];

for (const item of items) {
  const data = item.json;
  const title = (data.title || data['media:title'] || '').trim();
  const link = (data.link || data.url || '').trim();
  const pubDate = data.pubDate || data.published || data.isoDate || data.updated;
  const description = (data.contentSnippet || data.description || data.summary || '').replace(/<[^>]*>/g, '').trim().slice(0, 180);
  const source = data.feedTitle || data.channelTitle || 'Unknown';

  if (!title || !link) continue;
  if (seen.has(link)) continue;
  seen.add(link);

  // Recency filter
  if (pubDate) {
    const pub = new Date(pubDate);
    if (pub < cutoff) continue;
  }

  // Relevance score
  const combined = (title + ' ' + description).toLowerCase();
  let score = 0;
  for (const kw of keywords) {
    if (combined.includes(kw.toLowerCase())) score++;
  }
  if (score === 0) continue;

  results.push({
    json: {
      title,
      link,
      description: description || 'No preview available.',
      source,
      pubDate: pubDate ? new Date(pubDate).toLocaleString('en-US', { month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' }) : 'Recent',
      score
    }
  });
}

// Sort by score descending, cap at top 12
results.sort((a, b) => b.json.score - a.json.score);
return results.slice(0, 12);

// Build a sleek HTML email digest
const articles = $input.first().json.articles || [];
const today = new Date().toLocaleDateString('en-US', {
  weekday: 'long', year: 'numeric', month: 'long', day: 'numeric'
});

if (articles.length === 0) {
  return [{ json: { subject: `AI Digest â€” ${today}`, html: '<p>No relevant stories found in the last 24 hours.</p>' } }];
}

const bullets = articles.map(a => `
  <tr>
    <td style="padding: 16px 0; border-bottom: 1px solid #1e1e2e;">
      <a href="${a.link}" style="
        font-size: 15px;
        font-weight: 600;
        color: #c084fc;
        text-decoration: none;
        line-height: 1.4;
        display: block;
        margin-bottom: 4px;
      ">${a.title}</a>
      <p style="margin: 4px 0 6px; font-size: 13px; color: #94a3b8; line-height: 1.5;">${a.description}</p>
      <span style="
        font-size: 11px;
        color: #64748b;
        background: #1e1e2e;
        padding: 2px 8px;
        border-radius: 20px;
        margin-right: 6px;
      ">${a.source}</span>
      <span style="font-size: 11px; color: #64748b;">${a.pubDate}</span>
    </td>
  </tr>
`).join('');

const html = `
